export type BookNotificationKind = 'new_book' | 'file_upload' | 'prepress_comment';
/** Base fields shared by all book notifications */
export interface BookNotificationBase {
    bookTitle: string;
    /** Optional; e.g. "2/25/2026" */
    createdDate?: string;
}
/** New book registered in Acutrack Book Print */
export interface NewBookPayload extends BookNotificationBase {
    kind: 'new_book';
    pageCount?: number;
    /** Next steps text shown in the email */
    nextSteps?: string;
}
/** Customer uploaded files for a book */
export interface FileUploadPayload extends BookNotificationBase {
    kind: 'file_upload';
    /** Who uploaded (e.g. customer name or "A customer") */
    uploadedBy?: string;
    /** Short description of what was uploaded */
    uploadSummary?: string;
    nextSteps?: string;
}
/** Prepress team left a comment on a book */
export interface PrepressCommentPayload extends BookNotificationBase {
    kind: 'prepress_comment';
    /** Comment snippet or "Prepress team left a comment" */
    commentSummary?: string;
    nextSteps?: string;
}
export type BookNotificationPayload = NewBookPayload | FileUploadPayload | PrepressCommentPayload;
/** Default copy for each notification type */
export declare const DEFAULT_NEXT_STEPS: Record<BookNotificationKind, string>;
